<?phpdeclare(strict_types=1);namespace rameninfo\Application\Usecases\Ramen;use rameninfo\Domain\Models\Ramen\RamenRepository;final class DeleteRamen{    private $ramenRepo;    public function __construct(RamenRepository $ramenRepository)    {        $this->ramenRepo = $ramenRepository;    }    public function __invoke(string $ramenId = null)    {        return $this->ramenRepo->delete($ramenId);    }}